// __tests__/api/credits.test.ts
import { GET } from '@/app/api/credits/me/route';
import { mockAuth, mockDb } from '@/test-utils';

describe('/api/credits/me', () => {
  beforeEach(() => {
    mockAuth({ userId: 'user_123' });
  });

