}
```

### Authentication Hooks

```tsx
// hooks/use-auth.ts
import { useAuth as useClerkAuth, useUser } from '@clerk/nextjs';

export function useAuth() {
  const { isLoaded, isSignedIn, userId } = useClerkAuth();
  const { user } = useUser();

  return {
    isLoaded,
    isSignedIn,
    userId,
    user,
    isAuthenticated: isLoaded && isSignedIn,
  };
}

// Usage in components
