"use client";

import * as React from "react";
import { Moon, Sun, Laptop } from "lucide-react";
import { useTheme } from "./theme-provider";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

export function ThemeToggle() {
  const { theme, setTheme, resolvedTheme } = useTheme();

  const label =
    theme === "system" ? `System (${resolvedTheme})` : theme === "light" ? "Light" : "Dark";

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button variant="ghost" size="icon" aria-label={`Theme: ${label}`}>
          <Sun className="h-5 w-5 rotate-0 scale-100 transition-all dark:-rotate-90 dark:scale-0" />
          <Moon className="absolute h-5 w-5 rotate-90 scale-0 transition-all dark:rotate-0 dark:scale-100" />
          <span className="sr-only">Toggle theme</span>
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="end" className="w-40">
        <DropdownMenuItem onClick={() => setTheme("light")} aria-label="Switch to Light theme">
          <Sun className="mr-2 h-4 w-4" />
          Light
        </DropdownMenuItem>
        <DropdownMenuItem onClick={() => setTheme("dark")} aria-label="Switch to Dark theme">
          <Moon className="mr-2 h-4 w-4" />
          Dark
        </DropdownMenuItem>
        <DropdownMenuItem onClick={() => setTheme("system")} aria-label="Use System theme">
          <Laptop className="mr-2 h-4 w-4" />
          System
        </DropdownMenuItem>
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

src/contexts/page-metadata.tsx.content:
"use client";

import React, { createContext, useContext, useState, useCallback, ReactNode } from "react";

export interface BreadcrumbItem {
  label: string;
  href?: string;
}

export interface PageMetadata {
  title?: string;
  description?: string;
  breadcrumbs?: BreadcrumbItem[];
  showBreadcrumbs?: boolean;
}

interface PageMetadataContextType {
  metadata: PageMetadata;
  setMetadata: (metadata: PageMetadata) => void;
  updateMetadata: (metadata: Partial<PageMetadata>) => void;
}

const PageMetadataContext = createContext<PageMetadataContextType | undefined>(undefined);

export function PageMetadataProvider({ children }: { children: ReactNode }) {
  const [metadata, setMetadataState] = useState<PageMetadata>({
    showBreadcrumbs: true,
  });

  const setMetadata = useCallback((newMetadata: PageMetadata) => {
    setMetadataState(newMetadata);
  }, []);

  const updateMetadata = useCallback((partialMetadata: Partial<PageMetadata>) => {
    setMetadataState((prev) => ({ ...prev, ...partialMetadata }));
  }, []);

  return (
    <PageMetadataContext.Provider value={{ metadata, setMetadata, updateMetadata }}>
      {children}
    </PageMetadataContext.Provider>
  );
}

export function usePageMetadata() {
  const context = useContext(PageMetadataContext);
  if (context === undefined) {
    throw new Error("usePageMetadata must be used within a PageMetadataProvider");
  }
  return context;
}

// Hook para definir metadados da p√°gina
export function useSetPageMetadata(metadata: PageMetadata) {
  const { setMetadata } = usePageMetadata();

  React.useEffect(() => {
    setMetadata(metadata);
    // Depend√™ncias espec√≠ficas ao inv√©s do objeto completo para evitar loops
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [
    setMetadata,
    metadata.title,
    metadata.description,
    metadata.showBreadcrumbs,
    // Para arrays, usamos JSON.stringify para compara√ß√£o profunda
    // eslint-disable-next-line react-hooks/exhaustive-deps
    JSON.stringify(metadata.breadcrumbs)
  ]);
}

