"use client";

import { useMemo } from "react";
import { useSetPageMetadata, PageMetadata } from "@/contexts/page-metadata";

/**
 * Hook helper para configurar metadados da p√°gina de forma mais simples
 * 
 * @example
 * // Uso b√°sico
 * usePageConfig("Minha P√°gina", "Descri√ß√£o da p√°gina");
 * 
 * // Com breadcrumbs customizados
 * usePageConfig("Perfil", "Gerencie seu perfil", [
 *   { label: "In√≠cio", href: "/dashboard" },
 *   { label: "Perfil" }
 * ]);
 * 
 * // Com objeto completo
 * usePageConfig({
 *   title: "Dashboard",
 *   description: "Vis√£o geral",
 *   showBreadcrumbs: false
 * });
 */
export function usePageConfig(
  titleOrConfig: string | PageMetadata,
  description?: string,
  breadcrumbs?: PageMetadata["breadcrumbs"]
) {
  // Memoizar o objeto metadata para evitar recria√ß√µes desnecess√°rias
  const metadata = useMemo<PageMetadata>(() => {
    return typeof titleOrConfig === "string"
      ? {
          title: titleOrConfig,
          description,
          breadcrumbs,
        }
      : titleOrConfig;
  }, [titleOrConfig, description, breadcrumbs]);

  useSetPageMetadata(metadata);
}

src/hooks/use-storage.ts.content:
"use client";

import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { api } from '@/lib/api-client';

export interface StorageItem {
  id: string;
  name: string;
  contentType: string | null;
  size: number;
  url: string;
  pathname: string;
  createdAt: string;
  user: { id: string; clerkId: string; email: string | null; name: string | null };
}

export interface StorageParams {
  q?: string;
  type?: string;
  userId?: string;
  cursor?: string;
  limit?: number;
}

export interface StorageResponse {
  items: StorageItem[];
  nextCursor: string | null;
}

export function useStorage(params: StorageParams = {}) {
  const searchParams = new URLSearchParams();

  if (params.q) searchParams.set('q', params.q);
  if (params.type) searchParams.set('type', params.type);
  if (params.userId) searchParams.set('userId', params.userId);
  if (params.cursor) searchParams.set('cursor', params.cursor);
  if (params.limit) searchParams.set('limit', params.limit.toString());

  return useQuery<StorageResponse>({
    queryKey: ['admin-storage', params],
    queryFn: () => api.get(`/api/admin/storage?${searchParams.toString()}`),
    staleTime: 60_000, // 1 minute
    gcTime: 5 * 60_000, // 5 minutes
  });
}

export function useDeleteStorageItem() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: (id: string) =>
      api.delete(`/api/admin/storage/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['admin-storage'] });
    },
  });
}

