import { NextResponse } from 'next/server'
import { auth } from '@clerk/nextjs/server'
import { isAdmin } from '@/lib/admin-utils'
import { db } from '@/lib/db'

export async function GET(req: Request) {
  const { userId } = await auth()
  if (!userId || !(await isAdmin(userId))) {
    return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
  }
  const { searchParams } = new URL(req.url)
  const q = (searchParams.get('q') || '').trim().toLowerCase()
  const type = (searchParams.get('type') || '').trim().toLowerCase()
  const userIdFilter = (searchParams.get('userId') || '').trim()
  const limit = Math.min(100, Math.max(1, Number(searchParams.get('limit') || '50')))
  const cursor = searchParams.get('cursor') || undefined

  const where: Record<string, unknown> = {
    deletedAt: null,
  }
  if (q) {
    where.OR = [
      { name: { contains: q, mode: 'insensitive' } },
      { contentType: { contains: q, mode: 'insensitive' } },
      { pathname: { contains: q, mode: 'insensitive' } },
      { url: { contains: q, mode: 'insensitive' } },
      { user: { email: { contains: q, mode: 'insensitive' } } },
      { user: { name: { contains: q, mode: 'insensitive' } } },
    ]
  }
  if (type) {
    where.contentType = { contains: type, mode: 'insensitive' }
  }
  if (userIdFilter) {
    where.userId = userIdFilter
  }

  const items = await db.storageObject.findMany({
    where,
    include: { user: { select: { id: true, clerkId: true, email: true, name: true } } },
    orderBy: { createdAt: 'desc' },
    take: limit + 1,
    ...(cursor ? { cursor: { id: cursor }, skip: 1 } : {}),
  })
  const nextCursor = items.length > limit ? items[limit].id : null
  const page = items.slice(0, limit)
  return NextResponse.json({ items: page, nextCursor })
}

src/app/api/admin/usage/route.ts.content:
import { auth } from "@clerk/nextjs/server";
import { NextResponse } from "next/server";
import { db } from "@/lib/db";
import { isAdmin } from "@/lib/admin-utils";
import type { Prisma } from "../../../../../prisma/generated/client";

export async function GET(request: Request) {
  try {
    const { userId } = await auth();
    if (!userId || !(await isAdmin(userId))) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    const { searchParams } = new URL(request.url);
    const type = searchParams.get("type") || "all";
    const range = searchParams.get("range") || "7days";
    const q = (searchParams.get("q") || "").trim();
    const page = Math.max(1, Number(searchParams.get("page") || 1));
    const pageSize = Math.min(100, Math.max(1, Number(searchParams.get("pageSize") || 25)));

    const whereClause: Prisma.UsageHistoryWhereInput = { AND: [] };

    if (type !== "all") {
      (whereClause.AND as Prisma.UsageHistoryWhereInput[]).push({ 
        operationType: type as any 
      });
    }

    if (range !== "all") {
      const now = new Date();
      const startDate = new Date();
      switch (range) {
        case "24hours":
          startDate.setHours(now.getHours() - 24);
          break;
        case "7days":
          startDate.setDate(now.getDate() - 7);
          break;
        case "30days":
          startDate.setDate(now.getDate() - 30);
          break;
      }
      (whereClause.AND as Prisma.UsageHistoryWhereInput[]).push({ 
        timestamp: { gte: startDate } 
      });
    }

    if (q) {
      (whereClause.AND as Prisma.UsageHistoryWhereInput[]).push({
        user: {
          is: {
            OR: [
              { name: { contains: q, mode: 'insensitive' } },
              { email: { contains: q, mode: 'insensitive' } },
            ],
          },
        },
      });
    }

    const [total, usageHistory] = await Promise.all([
      db.usageHistory.count({ where: whereClause }),
      db.usageHistory.findMany({
        where: whereClause,
        include: {
          user: { select: { name: true, email: true } },
        },
        orderBy: { timestamp: "desc" },
        skip: (page - 1) * pageSize,
        take: pageSize,
      }),
    ]);

    return NextResponse.json({ data: usageHistory, total, page, pageSize });
  } catch (error) {
    console.error("Failed to fetch usage history:", error);
    return NextResponse.json(
      { error: "Failed to fetch usage history" },
      { status: 500 }
    );
  }
}

src/app/api/admin/users/route.ts.content:
import { auth } from "@clerk/nextjs/server";
import { NextResponse } from "next/server";
import { db } from "@/lib/db";
import { isAdmin } from "@/lib/admin-utils";
import { cache, getCacheKey } from "@/lib/cache";

export async function GET(request: Request) {
  try {
    const { userId } = await auth();

    if (!userId || !(await isAdmin(userId))) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    const { searchParams } = new URL(request.url);
    const page = Math.max(1, Number(searchParams.get("page") || 1));
    const pageSize = Math.min(100, Math.max(1, Number(searchParams.get("pageSize") || 50)));
    const search = searchParams.get("search")?.trim() || "";
    const includeUsageCount = searchParams.get("includeUsageCount") === "true";

    // Build where clause for search
    const whereClause = search ? {
      OR: [
        { name: { contains: search, mode: 'insensitive' as const } },
        { email: { contains: search, mode: 'insensitive' as const } },
      ]
    } : {};

    // Create cache key based on query parameters
    const cacheKey = getCacheKey('admin:users', page, pageSize, search, includeUsageCount.toString());

    // Try to get from cache first (only for non-search queries to avoid stale search results)
    if (!search) {
      const cached = cache.get(cacheKey);
      if (cached) {
        return NextResponse.json(cached);
      }
    }

    // Get total count and users in parallel
    const [total, users] = await Promise.all([
      db.user.count({ where: whereClause }),
      db.user.findMany({
        where: whereClause,
        include: {
          creditBalance: {
            select: {
              creditsRemaining: true,
            },
          },
          ...(includeUsageCount ? {
            _count: {
              select: {
                usageHistory: true,
              },
            },
          } : {}),
        },
        orderBy: {
          createdAt: "desc",
        },
        skip: (page - 1) * pageSize,
        take: pageSize,
      }),
    ]);

    const result = {
      users,
      pagination: {
        page,
        pageSize,
        total,
        pages: Math.ceil(total / pageSize),
      },
    };

    // Cache the result (only for non-search queries, cache for 2 minutes)
    if (!search) {
      cache.set(cacheKey, result, 120);
    }

    return NextResponse.json(result);
  } catch (error) {
    console.error("Failed to fetch users:", error);
    return NextResponse.json(
      { error: "Failed to fetch users" },
      { status: 500 }
    );
  }
}

