import { auth } from "@clerk/nextjs/server";
import { NextResponse } from "next/server";
import { db } from "@/lib/db";
import { isAdmin } from "@/lib/admin-utils";
import { z } from "zod";

export async function DELETE(
  request: Request,
  ctx: { params: Promise<{ id: string }> }
) {
  try {
    const { userId } = await auth();

    if (!userId || !(await isAdmin(userId))) {
      return NextResponse.json({ error: "N√£o autorizado" }, { status: 401 });
    }

    const { id } = await ctx.params
    const existing = await db.user.findUnique({ where: { id } })
    if (!existing) {
      return NextResponse.json({ error: 'Usu√°rio n√£o encontrado' }, { status: 404 })
    }

    try {
      const updated = await db.user.update({ where: { id }, data: { isActive: false } })
      return NextResponse.json({ success: true, user: { id: updated.id, isActive: updated.isActive } })
    } catch (e: unknown) {
      const msg = (e && ((e as { message?: string }).message || e.toString())) || 'Falha na atualiza√ß√£o'
      // Provide a clearer hint if the DB schema hasn't been migrated yet
      if (msg.includes('isActive') || msg.toLowerCase().includes('column') || msg.toLowerCase().includes('unknown')) {
        return NextResponse.json(
          { error: 'Schema do banco de dados desatualizado. Execute as migra√ß√µes para adicionar `User.isActive` (npm run db:migrate).' },
          { status: 409 }
        )
      }
      throw e
    }
  } catch {
    // console.error("Failed to delete user:", error);
    return NextResponse.json(
      { error: "Falha ao excluir usu√°rio" },
      { status: 500 }
    );
  }
}

const UpdateSchema = z
  .object({
    name: z.string().min(1).max(200).optional(),
    email: z.string().email().optional(),
  })
  .strict()

export async function PUT(
  request: Request,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const { userId } = await auth();
    if (!userId || !(await isAdmin(userId))) {
      return NextResponse.json({ error: "N√£o autorizado" }, { status: 401 });
    }

    const body = await request.json().catch(() => null);
    const parsed = UpdateSchema.safeParse(body);
    if (!parsed.success) {
      return NextResponse.json({ error: "Dados inv√°lidos", issues: parsed.error.flatten() }, { status: 400 });
    }
    const data = parsed.data;

    const { id } = await params;
    const existing = await db.user.findUnique({ where: { id } });
    if (!existing) {
      return NextResponse.json({ error: "Usu√°rio n√£o encontrado" }, { status: 404 });
    }

    const updated = await db.user.update({
      where: { id },
      data: {
        name: data.name ?? existing.name,
        email: data.email ?? existing.email,
      },
      include: {
        creditBalance: { select: { creditsRemaining: true } },
        _count: { select: { usageHistory: true } },
      },
    });

    return NextResponse.json(updated);
  } catch (error) {
    console.error("Failed to update user:", error);
    return NextResponse.json({ error: "Falha ao atualizar usu√°rio" }, { status: 500 });
  }
}

