src/components/fii/portfolio-upload-sect.content:
"use client"

import React, { useState } from "react";
import { PortfolioUploader } from "./portfolio-uploader";
import { usePortfolioUpload } from "@/hooks/use-portfolio-upload";
import { usePortfolioAnalysis } from "@/hooks/use-portfolio-analysis";
import { PortfolioAnalysisSection } from "./portfolio-analysis-section";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { FileSpreadsheet, TrendingUp, PieChart, Target, Brain } from "lucide-react";
import { formatCurrency } from "@/lib/utils";

export function PortfolioUploadSection() {
  const { uploading, uploadResult, uploadPortfolio, clearResult } = usePortfolioUpload();
  const [showAnalysis, setShowAnalysis] = useState(false);
  const { analyzePortfolio, isAnalyzing } = usePortfolioAnalysis(uploadResult?.id);

  const handleUpload = async (file: File) => {
    try {
      await uploadPortfolio(file);
      setShowAnalysis(false); // Reset analysis view when new upload
    } catch (error) {
      // Error is already handled in the hook
      console.error("Upload failed:", error);
    }
  };

  const handleAnalyzePortfolio = () => {
    if (uploadResult?.id) {
      analyzePortfolio(uploadResult.id);
      setShowAnalysis(true);
    }
  };

  const handleViewRecommendations = () => {
    setShowAnalysis(true);
  };

  if (uploadResult) {
    return (
      <div className="space-y-6">
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-2">
                <FileSpreadsheet className="h-5 w-5 text-green-600" />
                <CardTitle>Carteira Carregada com Sucesso!</CardTitle>
              </div>
              <Button
                variant="outline"
                size="sm"
                onClick={clearResult}
              >
                Carregar Nova Carteira
              </Button>
            </div>
            <CardDescription>
              Sua carteira foi processada e est√° pronta para an√°lise pelos agentes de IA.
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="grid gap-4 md:grid-cols-3 mb-6">
              <div className="bg-background border rounded-lg p-4">
                <div className="flex items-center gap-2">
                  <TrendingUp className="h-5 w-5 text-blue-600" />
                  <div>
                    <p className="text-sm text-muted-foreground">Valor Total</p>
                    <p className="text-lg font-semibold">
                      {formatCurrency(uploadResult.totalValue)}
                    </p>
                  </div>
                </div>
              </div>
              <div className="bg-background border rounded-lg p-4">
                <div className="flex items-center gap-2">
                  <PieChart className="h-5 w-5 text-green-600" />
                  <div>
                    <p className="text-sm text-muted-foreground">FIIs na Carteira</p>
                    <p className="text-lg font-semibold">{uploadResult.positionsCount}</p>
                  </div>
                </div>
              </div>
              <div className="bg-background border rounded-lg p-4">
                <div className="flex items-center gap-2">
                  <Target className="h-5 w-5 text-purple-600" />
                  <div>
                    <p className="text-sm text-muted-foreground">Linhas Processadas</p>
                    <p className="text-lg font-semibold">
                      {uploadResult.positionsCount + uploadResult.skippedRows}
                    </p>
                  </div>
                </div>
              </div>
            </div>

            {uploadResult.positions.length > 0 && (
              <div>
                <h4 className="font-medium mb-3">Posi√ß√µes Principais (Preview)</h4>
                <div className="space-y-2">
                  {uploadResult.positions.map((position, index) => (
                    <div key={index} className="flex items-center justify-between p-3 bg-muted/50 rounded-lg">
                      <div className="flex items-center gap-3">
                        <Badge variant="outline" className="font-mono">
                          {position.fiiCode}
                        </Badge>
                        {position.fiiName && (
                          <span className="text-sm text-muted-foreground">
                            {position.fiiName}
                          </span>
                        )}
                      </div>
                      <div className="text-right">
                        <div className="font-medium">
                          {formatCurrency(position.currentValue)}
                        </div>
                        <div className="text-sm text-muted-foreground">
                          {position.quantity.toLocaleString()} cotas
                          {position.percentage && ` ‚Ä¢ ${position.percentage.toFixed(1)}%`}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>

                {uploadResult.positionsCount > 5 && (
                  <p className="text-sm text-muted-foreground mt-2">
                    E mais {uploadResult.positionsCount - 5} posi√ß√µes...
                  </p>
                )}
              </div>
            )}

            {uploadResult.skippedRows > 0 && (
              <div className="mt-4 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
                <p className="text-sm text-yellow-800">
                  ‚ö†Ô∏è {uploadResult.skippedRows} linha(s) foram ignoradas devido a dados inv√°lidos ou incompletos.
                </p>
              </div>
            )}

            <div className="mt-6 flex gap-3">
              <Button 
                className="flex-1" 
                onClick={handleAnalyzePortfolio}
                disabled={isAnalyzing || !uploadResult?.id}
              >
                {isAnalyzing ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2" />
                    Analisando...
                  </>
                ) : (
                  <>
                    <Brain className="mr-2 h-4 w-4" />
                    Analisar Carteira
                  </>
                )}
              </Button>
              <Button 
                variant="outline" 
                className="flex-1"
                onClick={handleViewRecommendations}
                disabled={!uploadResult?.id}
              >
                <TrendingUp className="mr-2 h-4 w-4" />
                Ver Recomenda√ß√µes
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Show analysis section when requested */}
        {showAnalysis && uploadResult?.id && (
          <PortfolioAnalysisSection 
            portfolioId={uploadResult.id}
            portfolioName={"Sua Carteira"}
          />
        )}
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <FileSpreadsheet className="h-5 w-5 text-primary" />
            Upload da Sua Carteira
          </CardTitle>
          <CardDescription>
            Envie um arquivo Excel ou CSV com suas posi√ß√µes atuais em FIIs para an√°lise personalizada
          </CardDescription>
        </CardHeader>
        <CardContent>
          <PortfolioUploader onUpload={handleUpload} />

          <div className="mt-6 space-y-4">
            <h4 className="font-medium">Formato do Arquivo</h4>
            <div className="text-sm text-muted-foreground space-y-2">
              <p>
                <strong>Colunas obrigat√≥rias:</strong> C√≥digo do FII, Quantidade de Cotas, Pre√ßo M√©dio
              </p>
              <p>
                <strong>Colunas opcionais:</strong> Nome do FII, Pre√ßo Atual, Valor Total, Setor
              </p>
              <p>
                <strong>Formatos aceitos:</strong> .xlsx, .xls, .csv (m√°ximo 10MB)
              </p>
              <p>
                <strong>Exemplo de cabe√ßalhos:</strong> "C√≥digo", "Quantidade", "Pre√ßo M√©dio", "Valor Total"
              </p>
            </div>

            <div className="p-3 bg-blue-50 border border-blue-200 rounded-lg">
              <p className="text-sm text-blue-800">
                üí° <strong>Dica:</strong> O sistema reconhece automaticamente colunas em portugu√™s e ingl√™s. 
                N√£o precisa se preocupar com o formato exato dos cabe√ßalhos.
              </p>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

src/components/fii/portfolio-uploader.ts.content:
"use client"

import * as React from "react"
import { useDropzone } from "react-dropzone"
import { cn } from "@/lib/utils"
import { Upload, FileSpreadsheet, X, Check, AlertCircle } from "lucide-react"
import { Button } from "@/components/ui/button"
import { Progress } from "@/components/ui/progress"

interface PortfolioUploaderProps {
  className?: string
  onUpload?: (file: File) => Promise<void>
  accept?: string[]
  maxSizeMB?: number
}

export function PortfolioUploader({
  className,
  onUpload,
  accept = ['.xlsx', '.xls', '.csv'],
  maxSizeMB = 10,
}: PortfolioUploaderProps) {
  const [uploadStatus, setUploadStatus] = React.useState<'idle' | 'uploading' | 'success' | 'error'>('idle')
  const [uploadProgress, setUploadProgress] = React.useState(0)
  const [errorMessage, setErrorMessage] = React.useState<string>('')
  const [uploadedFile, setUploadedFile] = React.useState<File | null>(null)

  const handleUpload = async (files: File[]) => {
    const file = files[0]
    if (!file) return

    setUploadedFile(file)
    setUploadStatus('uploading')
    setUploadProgress(0)
    setErrorMessage('')

    try {
      // Simula√ß√£o de progresso
      const progressInterval = setInterval(() => {
        setUploadProgress(prev => {
          if (prev >= 90) {
            clearInterval(progressInterval)
            return 90
          }
          return prev + 10
        })
      }, 200)

      await onUpload?.(file)

      clearInterval(progressInterval)
      setUploadProgress(100)
      setUploadStatus('success')
    } catch (error) {
      setUploadStatus('error')
      setErrorMessage(error instanceof Error ? error.message : 'Erro ao fazer upload do arquivo')
      setUploadProgress(0)
    }
  }

  const {
    getRootProps,
    getInputProps,
    isDragActive,
    isDragReject,
    fileRejections,
  } = useDropzone({
    onDrop: handleUpload,
    accept: {
      'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet': ['.xlsx'],
      'application/vnd.ms-excel': ['.xls'],
      'text/csv': ['.csv'],
    },
    maxFiles: 1,
    maxSize: maxSizeMB * 1024 * 1024,
    disabled: uploadStatus === 'uploading',
  })

  const reset = () => {
    setUploadStatus('idle')
    setUploadProgress(0)
    setErrorMessage('')
    setUploadedFile(null)
  }

  return (
    <div className={cn("w-full", className)}>
      <div
        {...getRootProps()}
        className={cn(
          "relative border-2 border-dashed rounded-lg p-8 text-center cursor-pointer transition-colors",
          isDragActive && !isDragReject && "border-primary bg-primary/5",
          isDragReject && "border-destructive bg-destructive/5",
          uploadStatus === 'uploading' && "cursor-not-allowed opacity-60",
          uploadStatus === 'success' && "border-green-500 bg-green-50",
          uploadStatus === 'error' && "border-destructive bg-destructive/5",
          uploadStatus === 'idle' && "border-muted-foreground/25 hover:border-primary hover:bg-primary/5"
        )}
      >
        <input {...getInputProps()} />

        {uploadStatus === 'idle' && (
          <div className="space-y-4">
            <div className="mx-auto w-16 h-16 bg-muted rounded-full flex items-center justify-center">
              <Upload className="h-8 w-8 text-muted-foreground" />
            </div>
            <div>
              <h3 className="text-lg font-medium">
                {isDragActive ? 'Solte o arquivo aqui' : 'Envie sua carteira atual'}
              </h3>
              <p className="text-sm text-muted-foreground mt-2">
                Arraste e solte ou clique para selecionar um arquivo Excel (.xlsx, .xls) ou CSV
              </p>
              <p className="text-xs text-muted-foreground mt-1">
                M√°ximo {maxSizeMB}MB
              </p>
            </div>
          </div>
        )}

        {uploadStatus === 'uploading' && uploadedFile && (
          <div className="space-y-4">
            <div className="mx-auto w-16 h-16 bg-primary/10 rounded-full flex items-center justify-center">
              <FileSpreadsheet className="h-8 w-8 text-primary animate-pulse" />
            </div>
            <div>
              <h3 className="text-lg font-medium">Processando carteira...</h3>
              <p className="text-sm text-muted-foreground">{uploadedFile.name}</p>
              <Progress value={uploadProgress} className="mt-3 h-2" />
            </div>
          </div>
        )}

        {uploadStatus === 'success' && uploadedFile && (
          <div className="space-y-4">
            <div className="mx-auto w-16 h-16 bg-green-100 rounded-full flex items-center justify-center">
              <Check className="h-8 w-8 text-green-600" />
            </div>
            <div>
              <h3 className="text-lg font-medium text-green-700">Carteira carregada com sucesso!</h3>
              <p className="text-sm text-muted-foreground">{uploadedFile.name}</p>
              <Button
                onClick={reset}
                variant="outline"
                size="sm"
                className="mt-3"
              >
                Carregar nova carteira
              </Button>
            </div>
          </div>
        )}

        {uploadStatus === 'error' && (
          <div className="space-y-4">
            <div className="mx-auto w-16 h-16 bg-destructive/10 rounded-full flex items-center justify-center">
              <AlertCircle className="h-8 w-8 text-destructive" />
            </div>
            <div>
              <h3 className="text-lg font-medium text-destructive">Erro no upload</h3>
              <p className="text-sm text-muted-foreground">{errorMessage}</p>
              <Button
                onClick={reset}
                variant="outline"
                size="sm"
                className="mt-3"
              >
                Tentar novamente
              </Button>
            </div>
          </div>
        )}

        {fileRejections.length > 0 && (
          <div className="absolute top-2 right-2">
            <div className="bg-destructive/10 text-destructive text-xs px-2 py-1 rounded">
              Arquivo inv√°lido
            </div>
          </div>
        )}
      </div>

      {fileRejections.length > 0 && (
        <div className="mt-2 text-sm text-destructive">
          {fileRejections[0].errors[0].message}
        </div>
      )}
    </div>
  )
}

src/hooks/admin/use-admin-recommended-po.content:
"use client";

import { useState, useEffect } from "react";
import { toast } from "sonner";
import { FiiSector } from "@prisma/client";

export type RecommendedPortfolio = {
  id: string;
  fiiCode: string;
  fiiName: string;
  sector: FiiSector;
  percentage: number;
  reasoning?: string;
  isActive: boolean;
  createdAt: Date;
  updatedAt: Date;
};

export type CreatePortfolioData = {
  fiiCode: string;
  fiiName: string;
  sector: FiiSector;
  percentage: number;
  reasoning?: string;
  isActive?: boolean;
};

export type UpdatePortfolioData = Partial<CreatePortfolioData>;

export function useAdminRecommendedPortfolio(
  page: number = 1,
  pageSize: number = 50,
  search: string = "",
  sector?: string,
  isActive?: string
) {
  const [data, setData] = useState<{
    portfolios: RecommendedPortfolio[];
    pagination: {
      page: number;
      pageSize: number;
      total: number;
      pages: number;
    };
    totalPercentage: number;
  } | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const fetchData = async () => {
    setLoading(true);
    setError(null);
    try {
      const params = new URLSearchParams({
        page: page.toString(),
        pageSize: pageSize.toString(),
        ...(search && { search }),
        ...(sector && { sector }),
        ...(isActive && { isActive }),
      });

      const response = await fetch(`/api/admin/recommended-portfolio?${params}`);
      if (!response.ok) {
        throw new Error(`Error: ${response.status} ${response.statusText}`);
      }
      const result = await response.json();
      setData(result);
    } catch (err) {
      console.error("Failed to fetch recommended portfolio:", err);
      setError(err instanceof Error ? err.message : "Unknown error");
      toast.error("Falha ao carregar carteira recomendada");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [page, pageSize, search, sector, isActive]);

  return {
    data,
    loading,
    error,
    refetch: fetchData,
  };
}

export function useCreateRecommendedPortfolio() {
  const [loading, setLoading] = useState(false);

  const createPortfolio = async (data: CreatePortfolioData): Promise<boolean> => {
    setLoading(true);
    try {
      const response = await fetch('/api/admin/recommended-portfolio', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.error || `Error: ${response.status} ${response.statusText}`);
      }

      toast.success("FII adicionado √† carteira recomendada com sucesso!");
      return true;
    } catch (err) {
      console.error("Failed to create portfolio entry:", err);
      const message = err instanceof Error ? err.message : "Erro desconhecido";
      toast.error(`Falha ao adicionar FII: ${message}`);
      return false;
    } finally {
      setLoading(false);
    }
  };

  return {
    createPortfolio,
    loading,
  };
}

export function useUpdateRecommendedPortfolio() {
  const [loading, setLoading] = useState(false);

  const updatePortfolio = async (id: string, data: UpdatePortfolioData): Promise<boolean> => {
    setLoading(true);
    try {
      const response = await fetch(`/api/admin/recommended-portfolio/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.error || `Error: ${response.status} ${response.statusText}`);
      }

      toast.success("FII atualizado com sucesso!");
      return true;
    } catch (err) {
      console.error("Failed to update portfolio entry:", err);
      const message = err instanceof Error ? err.message : "Erro desconhecido";
      toast.error(`Falha ao atualizar FII: ${message}`);
      return false;
    } finally {
      setLoading(false);
    }
  };

  return {
    updatePortfolio,
    loading,
  };
}

export function useDeleteRecommendedPortfolio() {
  const [loading, setLoading] = useState(false);

  const deletePortfolio = async (id: string): Promise<boolean> => {
    setLoading(true);
    try {
      const response = await fetch(`/api/admin/recommended-portfolio/${id}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.error || `Error: ${response.status} ${response.statusText}`);
      }

      toast.success("FII removido da carteira recomendada com sucesso!");
      return true;
    } catch (err) {
      console.error("Failed to delete portfolio entry:", err);
      const message = err instanceof Error ? err.message : "Erro desconhecido";
      toast.error(`Falha ao remover FII: ${message}`);
      return false;
    } finally {
      setLoading(false);
    }
  };

  return {
    deletePortfolio,
    loading,
  };
}

