  riskLevel: 'BAIXO' | 'MODERADO' | 'ALTO';
  diversificationScore: number;
  concentrationRisk: number;
  sectorAnalysis: {
    distribution: Record<string, number>;
    recommendations: string[];
  };
  performanceAnalysis: {
    totalValue: number;
    strongPositions: string[];
    weakPositions: string[];
  };
  recommendations: string[];
