'use client';

import React from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Separator } from '@/components/ui/separator';
import { usePortfolioAnalysis, type FIIAnalysisResult } from '@/hooks/use-portfolio-analysis';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';
import { TrendingUp, AlertTriangle, CheckCircle, Brain, Target, PieChart as PieChartIcon } from 'lucide-react';
import { formatCurrency } from '@/lib/utils';

interface PortfolioAnalysisSectionProps {
  portfolioId: string;
  portfolioName?: string;
}

const COLORS = ['#D4AF37', '#B8860B', '#DAA520', '#FFD700', '#F4E87C', '#FFF8DC'];

const getRiskColor = (riskLevel: string) => {
  switch (riskLevel) {
    case 'BAIXO': return 'bg-green-100 text-green-800';
    case 'MODERADO': return 'bg-yellow-100 text-yellow-800';
    case 'ALTO': return 'bg-red-100 text-red-800';
    default: return 'bg-gray-100 text-gray-800';
  }
};

const getScoreColor = (score: number) => {
  if (score >= 80) return 'text-green-600';
  if (score >= 60) return 'text-yellow-600';
  return 'text-red-600';
};

export function PortfolioAnalysisSection({ portfolioId, portfolioName }: PortfolioAnalysisSectionProps) {
  const {
    analysis,
    isAnalyzing,
    isLoadingAnalysis,
    analysisError,
    analyzePortfolio,
    hasAnalysis,
    canAnalyze,
    createdAt
  } = usePortfolioAnalysis(portfolioId);

  const handleAnalyze = () => {
    analyzePortfolio(portfolioId);
  };

  if (isLoadingAnalysis) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Brain className="h-5 w-5 text-primary" />
            An√°lise Inteligente
          </CardTitle>
          <CardDescription>
            Carregando an√°lise da carteira...
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="flex items-center justify-center py-12">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
          </div>
        </CardContent>
      </Card>
    );
  }

  if (analysisError && !hasAnalysis) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Brain className="h-5 w-5 text-primary" />
            An√°lise Inteligente
          </CardTitle>
          <CardDescription>
            An√°lise avan√ßada da sua carteira de FIIs
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="text-center py-8">
            <AlertTriangle className="h-12 w-12 text-yellow-500 mx-auto mb-4" />
            <h3 className="text-lg font-semibold mb-2">An√°lise n√£o encontrada</h3>
            <p className="text-muted-foreground mb-4">
              Nenhuma an√°lise foi encontrada para esta carteira.
            </p>
            <Button 
              onClick={handleAnalyze}
              disabled={!canAnalyze}
              className="bg-primary hover:bg-primary/90"
            >
