  if (!analysis) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Brain className="h-5 w-5 text-primary" />
            An√°lise Inteligente
          </CardTitle>
          <CardDescription>
            Use nossa IA para analisar sua carteira de FIIs
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="text-center py-8">
            <Brain className="h-12 w-12 text-primary/60 mx-auto mb-4" />
            <h3 className="text-lg font-semibold mb-2">An√°lise Inteligente</h3>
            <p className="text-muted-foreground mb-4">
              Nossa IA analisar√° diversifica√ß√£o, riscos, setores e dar√° recomenda√ß√µes personalizadas.
            </p>
            <Button 
              onClick={handleAnalyze}
              disabled={!canAnalyze}
              className="bg-primary hover:bg-primary/90"
            >
              {isAnalyzing ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2" />
                  Analisando...
                </>
              ) : (
                <>
                  <Brain className="h-4 w-4 mr-2" />
                  Analisar Carteira
                </>
              )}
            </Button>
          </div>
        </CardContent>
      </Card>
    );
  }

  // Prepare sector data for charts
  const sectorData = Object.entries(analysis.sectorAnalysis.distribution).map(([sector, percentage]) => ({
    sector,
    percentage: Number(percentage.toFixed(1))
  }));

  return (
    <div className="space-y-6">
      {/* Header */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle className="flex items-center gap-2">
                <Brain className="h-5 w-5 text-primary" />
                An√°lise Inteligente
              </CardTitle>
              <CardDescription>
                {portfolioName && `${portfolioName} ‚Ä¢ `}
                {createdAt && `An√°lise de ${new Date(createdAt).toLocaleDateString('pt-BR')}`}
              </CardDescription>
            </div>
            <Button 
              variant="outline" 
              onClick={handleAnalyze}
              disabled={!canAnalyze}
              size="sm"
            >
              {isAnalyzing ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-primary mr-2" />
                  Analisando...
                </>
              ) : (
                'Nova An√°lise'
              )}
