        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            {/* Overall Score */}
            <div className="text-center">
              <div className={`text-3xl font-bold ${getScoreColor(analysis.overallScore)}`}>
                {analysis.overallScore}
              </div>
              <div className="text-sm text-muted-foreground">Nota Geral</div>
              <Progress value={analysis.overallScore} className="mt-2" />
            </div>

            {/* Risk Level */}
            <div className="text-center">
              <Badge className={getRiskColor(analysis.riskLevel)}>
                {analysis.riskLevel}
              </Badge>
              <div className="text-sm text-muted-foreground mt-2">N√≠vel de Risco</div>
            </div>

            {/* Diversification */}
            <div className="text-center">
              <div className={`text-2xl font-bold ${getScoreColor(analysis.diversificationScore)}`}>
                {analysis.diversificationScore}
              </div>
              <div className="text-sm text-muted-foreground">Diversifica√ß√£o</div>
              <Progress value={analysis.diversificationScore} className="mt-2" />
            </div>

            {/* Concentration Risk */}
            <div className="text-center">
              <div className="text-2xl font-bold text-orange-600">
                {analysis.concentrationRisk}%
              </div>
              <div className="text-sm text-muted-foreground">Concentra√ß√£o</div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Summary */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <CheckCircle className="h-5 w-5 text-green-600" />
            Resumo Executivo
          </CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-muted-foreground leading-relaxed">
            {analysis.summary}
          </p>
        </CardContent>
      </Card>

      {/* Sector Analysis */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <PieChartIcon className="h-5 w-5 text-primary" />
              Distribui√ß√£o por Setor
            </CardTitle>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={300}>
              <PieChart>
                <Pie
                  data={sectorData}
                  cx="50%"
                  cy="50%"
                  labelLine={false}
                  label={({ sector, percentage }) => `${sector}: ${percentage}%`}
                  outerRadius={80}
                  fill="#8884d8"
                  dataKey="percentage"
                >
                  {sectorData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip />
              </PieChart>
            </ResponsiveContainer>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <TrendingUp className="h-5 w-5 text-green-600" />
              Performance
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <div className="flex justify-between items-center mb-2">
                <span className="text-sm font-medium">Valor Total</span>
                <span className="text-lg font-bold text-primary">
                  {formatCurrency(analysis.performanceAnalysis.totalValue)}
                </span>
              </div>
            </div>

            <Separator />

